import React from 'react';
import './App.css';

let questions = [
  {
    'id': 1,
    'question': 'Exemple question ???',
    'reponses': [
      ['réponse a', false],
      ['réponse b', false],
      ['réponse c', true],
      ['réponse d', false]
    ]
  }
];

class Question extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      question : questions
    }
  }

  componentDidMount(){
    this.setState({questions: questions[0]})
  }

  render() {
    return (
      <div>
        <p className="question">{this.state.id}. {this.state.question}</p>
        <button>{this.state.reponses[0]}</button>
        <button>{this.state.reponses[1]}</button>
        <button>{this.state.reponses[2]}</button>
        <button>{this.state.reponses[3]}</button>

        <button>Valider</button>
      </div>
    )
  }
}

class App extends React.Component {
  constructor(props){
    super(props);

    this.startQuiz = this.startQuiz.bind(this);
  }

  startQuiz(e){
    e.preventDefault();
    return document.getElementById("main").innerHTML = <Question />;
  }

  render() {
  return (
    <div id="main">
      <h1>Bienvenue !</h1>
      <button onClick={this.startQuiz}>Commencer le quiz</button>
    </div>
  )};
};

export default App;
