import React from 'react';
import './App.css';

let questions = [
  {
    'id': 1,
    'question': 'Exemple question ???',
    'reponses': [
      ['réponse a', false],
      ['réponse b', false],
      ['réponse c', true],
      ['réponse d', false]
    ]
  },
  {
    'id': 2,
    'question': 'Exemple question 2 ???',
    'reponses': [
      ['réponse a', true],
      ['réponse b', false],
      ['réponse c', false],
      ['réponse d', false]
    ]
  }
];

class Question extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      question : questions[0],
      reponse : "",
      score : this.props.score
    }

    this.choixReponse = this.choixReponse.bind(this);
    this.validate = this.validate.bind(this);
    this.nextQuestion = this.nextQuestion.bind(this);
  };

  choixReponse(e){
    e.preventDefault();
    this.setState({reponse: parseInt(e.target.id)});
  }

  validate(e){
    e.preventDefault();

    if(this.state.question.reponses[this.state.reponse][1] === true){
      let newScore = this.state.score + 1
      console.log(newScore)
      console.log(this.state.score)
      document.getElementById(this.state.reponse).style.border = "2px solid green";
      return this.setState({score: newScore + 1});
    } else {
      document.getElementById(this.state.reponse).style.border = "2px solid red";
      let index = this.state.question.reponses.indexOf(this.state.question.reponses.filter(item => item[1] === true)[0]);
      document.getElementById(index).style.border = "2px solid green";
    }
  }

  nextQuestion(e){
    e.preventDefault();
    document.querySelectorAll("#reponses button").forEach(item => item.style.border = null);
    if(questions[questions.indexOf(this.state.question) + 1]){
      this.setState({question: questions[questions.indexOf(this.state.question) + 1]});
    } else {
      this.props.getScore(this.state.score)
      return this.props.getScorePage(e);
    }
  }

  render() {
    return (
      <div>
        <p className="question">{this.state.question.id}. {this.state.question.question}</p>
        <div id="reponses">
          <button id="0" onClick={this.choixReponse}>{this.state.question.reponses[0][0]}</button>
          <button id="1" onClick={this.choixReponse}>{this.state.question.reponses[1][0]}</button>
          <button id="2" onClick={this.choixReponse}>{this.state.question.reponses[2][0]}</button>
          <button id="3" onClick={this.choixReponse}>{this.state.question.reponses[3][0]}</button>
        </div>

        <button onClick={this.validate}>Valider</button>
        <button onClick={this.nextQuestion}>Suivant</button>
      </div>
    )
  }
};

class ScoreScreen extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      score: this.props.score
    }
  }

  render(){

    return (
      <div>
        <h2>Votre score :</h2>
        <p>{this.props.score}</p>
      </div>
    )
  }
}

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      content: <div><h1>Bienvenue !</h1><button onClick={this.startQuiz}>Commencer le quiz</button></div>,
      score : 0
    }

    this.startQuiz = this.startQuiz.bind(this);
    this.getScore = this.getScore.bind(this);
    this.getScorePage = this.getScorePage.bind(this);

  }

  componentDidMount(){
    this.setState({content: <div><h1>Bienvenue !</h1><button onClick={this.startQuiz}>Commencer le quiz</button></div>})
  }

  startQuiz(e){
    e.preventDefault()
    this.setState({content: <Question score={this.state.score} getScore={this.getScore} getScorePage={this.getScorePage}/>})
  }

  getScore(val){
    this.setState({score: val});
  }

  getScorePage(e){
    e.preventDefault();
    this.setState({content: <ScoreScreen score={this.state.score}/>})
  }

  render() {
  return (
    <div id="main">
      {this.state.content}
    </div>
  )};
};

export default App;
