import React from 'react';
import './App.css';

let questions = [
  {
    'id': 1,
    'question': 'Exemple question ???',
    'reponses': [
      ['réponse a', false],
      ['réponse b', false],
      ['réponse c', true],
      ['réponse d', false]
    ]
  },
  {
    'id': 2,
    'question': 'Exemple question 2 ???',
    'reponses': [
      ['réponse a', true],
      ['réponse b', false],
      ['réponse c', false],
      ['réponse d', false]
    ]
  }
];

class Question extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      question : questions[0]
    }

    this.nextQuestion = this.nextQuestion.bind(this);
  };

  nextQuestion(e){
    e.preventDefault();
    this.setState({question: questions[questions.indexOf(this.state.question) + 1]});
  }

  render() {
    return (
      <div>
        <p className="question">{this.state.question.id}. {this.state.question.question}</p>
        <button>{this.state.question.reponses[0][0]}</button>
        <button>{this.state.question.reponses[1][0]}</button>
        <button>{this.state.question.reponses[2][0]}</button>
        <button>{this.state.question.reponses[3][0]}</button>

        <button>Valider</button>
        <button onClick={this.nextQuestion}>Suivant</button>
      </div>
    )
  }
};

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      content: <div><h1>Bienvenue !</h1><button onClick={this.startQuiz}>Commencer le quiz</button></div>
    }

    this.startQuiz = this.startQuiz.bind(this);
  }

  componentDidMount(){
    this.setState({content: <div><h1>Bienvenue !</h1><button onClick={this.startQuiz}>Commencer le quiz</button></div>})
  }

  startQuiz(e){
    e.preventDefault()
    this.setState({content: <Question />})
  }

  render() {
  return (
    <div id="main">
      {this.state.content}
    </div>
  )};
};

export default App;
