import React from 'react';
import './App.css';

let questions = [
  {
    'id': 1,
    'question': 'Exemple question ???',
    'reponses': [
      ['réponse a', false],
      ['réponse b', false],
      ['réponse c', true],
      ['réponse d', false]
    ]
  }
];

class Question extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      question : questions[0]
    }
  }

  componentDidMount(){
    this.setState({questions: questions[0]})
  }

  render() {
    return (
      <div>
        <p className="question">{this.state.question.id}. {this.state.question.question}</p>
        <button>{this.state.question.reponses[0][0]}</button>
        <button>{this.state.question.reponses[1][0]}</button>
        <button>{this.state.question.reponses[2][0]}</button>
        <button>{this.state.question.reponses[3][0]}</button>

        <button>Valider</button>
      </div>
    )
  }
};

class Home extends React.Component {
  constructor(props){
    super(props);
  }

  render(){

    return (
      <div>
        <h1>Bienvenue !</h1>
        <button onClick={props.startQuiz}>Commencer le quiz</button>
      </div>
    )
  }
}

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      content : <Home startQuiz={this.startQuiz} />
    }

    this.startQuiz = this.startQuiz.bind(this);
  }

  startQuiz(e){
    e.preventDefault()
    return this.setState({content: <Question />})
  }

  render() {
  return (
    <div id="main">
      {this.state.content}
    </div>
  )};
};

export default App;
